import "./style.css";
import { useState } from "react";

export const BotonRow = ({ value }) => {
  const [valueX, putX] = useState(0);

  const setX = () => {
    putX("X");
  };

  return (
    <>
      <button onClick={setX} className="Square">{valueX}</button>
    </>
  );
};

export const Square = () => {
  return (
    <>
      <div className="boardRow">
        <BotonRow />
      </div>

      <div className="boardRow">
        <BotonRow value={3} />
      </div>

      <div className="boardRow">
        <BotonRow value={6} />
      </div>
    </>
  );
};
