import "./style.css";
import { useState } from "react";

export const BotonRow = ({ value }) => {
  const [charX, putX] = useState(0);

  return (
    <>
      <button onClick={value=putX(0)} className="Square">
        {value + 1}
      </button>
      <button onClick={value=putX(0)} className="Square">
        {value + 2}
      </button>
      <button onClick={value=putX(0)} className="Square">
        {value + 3}
      </button>
    </>
  );
};

export const Square = () => {
  return (
    <>
      <div className="boardRow">
        <BotonRow value={0} />
      </div>

      <div className="boardRow">
        <BotonRow value={3} />
      </div>

      <div className="boardRow">
        <BotonRow value={6} />
      </div>
    </>
  );
};
